package idat.edu.pe;

public class Alumno {
	

	private int codigo;
	private String nombre;
	private int edad;
	
	public Alumno() {
		// TODO Auto-generated constructor stub
	}

	public int getCodigo() {
		return codigo;
	}

	public String getNombre() {
		return nombre;
	}

	public int getEdad() {
		return edad;
	}

	public Alumno(int codigo, String nombre, int edad) {
		super();
		this.codigo = codigo;
		this.nombre = nombre;
		this.edad = edad;
	}
	
	
	
		
	
	
	
	
}
